Energy = Energy(:);
if (ischar   (Material)) Material = {Material}; end
if (isnumeric(Material)) Material = num2cell(Material); end

nMaterial = length(Material);
nEnergy = length(Energy);
nData   = length(xRayAttenuationData);
Name    = cell (1      ,nMaterial);
Density = zeros(1      ,nMaterial);
MAC     = zeros(nEnergy,nMaterial);
MEAC    = zeros(nEnergy,nMaterial);

if (nEnergy>0 && (any(Energy<0.001) || any(Energy>20)))
    warning('Error in PhotonAttenuation function: energy is outside of the recomended range from 0.001 MeV to 20 MeV');
end

for i = 1:nMaterial
    material = Material{i};
    if (ischar(material))
        for k = 1:nData
            if (strcmpi(material, xRayAttenuationData{k}.ShortName) || ...
                    strcmpi(material, xRayAttenuationData{k}.LongName ) )
                material = k;
                break;
            end
        end
    end
    
    if (ischar(material) || any(material<1) || any(material>nData))
        error('Error in PhotonAttenuation function: material was not recognized');
    end
    
    material = round(material);
    X = xRayAttenuationData{material};
    if (nEnergy) % if energies given
        E = log(X.PhotonEnergy);
        A = log([X.MAC', X.MEAC']);
        
        d = 1e-6;            % deal with compton edges
        k = find(diff(E)==0);% find compton edges
        E(k  ) = E(k  )-d;   % give them some width
        E(k+1) = E(k+1)+d;
        a = interp1(E, A, log(Energy), 'linear');
        a(a>9.267) = 9.267;  % extrapolated values can get high especially close to compton edges. Trim them.
        MAC  (:,i) = exp(a(:,1));
        if (size(a,2)==2) MEAC (:,i) = exp(a(:,2)); end
    end
    ShortName{i} = X.ShortName;
    LongName{i}  = X.LongName;
    Density(i)   = X.Density;
end
